	, length
	, replacement_cost
	, rating
	, special_features
) VALUES (
	#{title}
	, #{description}
	, #{releaseYear}
	, #{languageId}
	, #{originalLanguageId}
	, #{rentalDuration}
	, #{rentalRate}
	, #{length}
	, #{replacementCost}
	, #{rating}
	, #{specialFeatures}
)
